    const removeItemToCart=(id)=>{}
    const cartContext={
        items:[],
        totlaAmount:0,
        addItem:addItemToCart,
        removeItem:removeItemToCart
    }
return <CartContext.Provider value={cartContext}>
    {props.children}
</CartContext.Provider>
}

export default CartProvider;
